document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".modal-menu"),s=document.querySelector(".hamburgers-show"),t=document.querySelector(".hamburgers-close");s.addEventListener("click",()=>{e.classList.remove("hide"),e.classList.add("show")}),t.addEventListener("click",()=>{e.classList.remove("show"),e.classList.add("hide")})});